	(26532, 2, 1, 'Not quick enough!', 14, 0, 100, 0, 0, 13411, 'epoch SAY_TIME_WARP_2'),
	(26532, 2, 2, 'Let\'s get this over with. ', 14, 0, 100, 0, 0, 13412, 'epoch SAY_TIME_WARP_3'),
