	(26532, 4, 0, '', 12, 0, 100, 0, 0, 13416, 'epoch SAY_DEATH'),
	(26530, 0, 0, 'Ah, the entertainment has arrived!', 14, 0, 100, 0, 0, 13472, 'salramm SAY_AGGRO'),
