	(26530, 2, 0, 'The fun is just beginning!', 14, 0, 100, 0, 0, 13473, 'salramm SAY_SLAY_1'),
	(26530, 2, 1, 'Aah, quality materials!', 14, 0, 100, 0, 0, 13474, 'salramm SAY_SLAY_2'),
	(26530, 2, 2, 'Don\'t worry, I\'ll make good use of you.', 14, 0, 100, 0, 0, 13475, 'salramm SAY_SLAY_3'),
	(26530, 3, 0, 'You only advance... the master\'s plan...', 14, 0, 100, 0, 0, 13483, 'salramm SAY_DEATH'),
	(26530, 4, 0, 'BOOM! Hahahahah...', 14, 0, 100, 0, 0, 13478, 'salramm SAY_EXPLODE_GHOUL_1'),
	(26530, 4, 1, 'Blood... destruction... EXHILARATING!', 14, 0, 100, 0, 0, 13479, 'salramm SAY_EXPLODE_GHOUL_2'),
