	(26588, 3, 0, 'He\'s gonna eat Mr. Floppy! You gotta help Mr. Floppy! You just gotta!', 12, 0, 100, 0, 0, 0, 'emily SAY_WORGRAGGRO4'),
	(26588, 4, 0, 'There\'s a big meanie attacking Mr. Floppy! Help! ', 12, 0, 100, 0, 0, 0, 'emily SAY_RANDOMAGGRO'),
	(26588, 5, 0, 'Let\'s get out of here before more wolves find us!', 12, 0, 100, 0, 0, 0, 'emily SAY_VICTORY1'),
