	(26588, 7, 0, 'Mr. Floppy, you\'re ok! Thank you so much for saving Mr. Floppy!', 12, 0, 100, 0, 0, 0, 'emily SAY_VICTORY3'),
	(26588, 8, 0, 'I think I see the camp! We\'re almost home, Mr. Floppy! Let\'s go!', 12, 0, 100, 0, 0, 0, 'emily SAY_VICTORY4'),
	(26588, 9, 0, 'Mr. Floppy revives', 15, 0, 100, 0, 0, 0, 'emily TEXT_EMOTE_WP1'),
	(26588, 10, 0, 'The Ravenous Worg chomps down on Mr. Floppy', 15, 0, 100, 0, 0, 0, 'emily TEXT_EMOTE_AGGRO'),
